import React, { useState, useEffect } from 'react';
import { Button, Table } from "react-bootstrap";
import "./MainPage.css";
// import Modal from "./Modal";
import $ from 'jquery';

function MainPage() {
    // const [items, setItems] = useState(null);
    const [selected, setSelected] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [checkBoxDisabled, setCheckBoxDisabled] = useState(false);


    const updateQuantity = (e) => {
        // setCheckBoxDisabled(true);
        // console.log(e);
        // var selected = new Array();
        // $('.ItemTable [type="checkbox"]').each(function (i, chk) {
        //     if (chk.checked) {
        //         selected.push(i);
        //     }
        // });
        // setTimeout(function () { console.log(selected); }, 3000);
        // console.log(selected);
        // if (selected.length == 0) {
        //     alert("No items are selected.")
        // }
    }

    const handleInputChange = (e) => {

        let value = e.target.value;
        if (e.target.checked) {
            selected.push(value);
        }
        else {
            let index = selected.indexOf(value);
            console.log(index);
            if (index > 0) {
                setSelected(selected.splice(index, 1));
            }
        }
        console.log(selected);

    }
    // 
    const ItemTable = () => (
        <Table striped bordered hover size="sm" className="ItemTable" >
            <thead>
                <tr>
                    <th></th>
                    <th>Item Name</th>
                    <th>Quantity</th>
                </tr>
            </thead>
            <tbody>
                {items.map(function (item, i = 0) {
                    return <tr>
                        <td><input type="checkbox" disabled={checkBoxDisabled} onChange={handleInputChange} value={i++} /></td>
                        <td>{item.name}</td>
                        <td>{item.quantity}</td>
                    </tr>
                })}
            </tbody>
        </Table>
    );

    const items = [
        { name: 'nick', quantity: 0 },
        { name: 'asdasdasd', quantity: 0 },
        { name: 'asdasd', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 }
    ];

    return (

        <div className="MainPageDiv">
            <div className="ButtonDiv">
                <Button onClick={updateQuantity} action="add" variant="success">Add Item</Button>
                <Button onClick={updateQuantity} action="delete" variant="danger">Delete Item</Button>
            </div>
            {/* <Modal showModal={showModal} /> */}
            <ItemTable />
        </div>
    );
}

export default MainPage;
