import React, { useState, useEffect } from 'react';
import { Button, Form, FormControl, Modal } from "react-bootstrap";

const AddModal = (props) => {

    const [saveButtonDisabled, setSaveButtonDisabled] = useState(false);
    const handleClose = () => {
        props.setShowModal(false);
    }
    const validateQuantity = () => {
        setSaveButtonDisabled(true);
    }
    return (
        <Modal show={props.showModal} animation={false}
            onHide={() => props.setShowModal(false)}
        >
            <Modal.Header closeButton>
                <Modal.Title>Register Item</Modal.Title>
            </Modal.Header>

            <Modal.Body>
                <Form.Group>
                    <Form.Label>Enter Item Name</Form.Label>
                    <Form.Control type="textarea" placeholder="Item name" />
                    <br />
                    <Form.Label>Enter Quantity</Form.Label>
                    <Form.Control type="number" placeholder="Quantity" onChange={saveButtonDisabled} />
                    <Form.Text className="text-muted">
                        Valid quantities 1-99
                    </Form.Text>

                </Form.Group>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}
                >
                    Cancel
                  </Button>
                <Button variant="primary" onClick={handleClose} disabled={true}
                >
                    Save Changes
                  </Button>
            </Modal.Footer>
        </Modal>
    );
}

export default AddModal;
