import React, { useState, useEffect } from 'react';
import { Button, Table } from "react-bootstrap";
import MainPage from "./MainPage.css"
import $ from 'jquery';

function App() {
    // const [items, setItems] = useState(null);
    const [toBeDeleted, setToBeDeleted] = useState([]);
    const retrieveListItems = () => {

        // make api call to get items

        // set data to state hook
    }
    const checkBoxChanged = (e) => {

        const itemName = e.target.getAttribute("itemname");
        const itemQuantity = e.target.getAttribute("itemquantity");

        const item = {
            name: itemName,
            quantity: itemQuantity
        };
        items.push(item);
    }
    const updateQuantity = (e) => {

        var selected = new Array();
        $('table [type="checkbox"]').each(function (i, chk) {
            if (chk.checked) {
                console.log("Checked!", i, chk);
            }
        });

    }


    // make api call to get the list of items
    useEffect(() => {
        retrieveListItems();
    }, []);

    // 
    const ItemTable = () => (
        <Table striped bordered hover size="sm">
            <thead>
                <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Username</th>
                </tr>
            </thead>
            <tbody>
                {items.map(function (item) {
                    return <tr>
                        <td><input type="checkbox" /></td>
                        <td>{item.name}</td>
                        <td>{item.quantity}</td>
                    </tr>


                })}
            </tbody>
        </Table>
        // <Table striped bordered hover><tr>
        //     <th>Select</th>
        //     <th>Item Name</th>
        //     <th>Quantity</th>
        // </tr>
        //     {items.map(function (item) {
        //         return <tr>
        //             <td><input type="checkbox" /></td>
        //             <td>{item.name}</td>
        //             <td>{item.quantity}</td>
        //         </tr>


        //     })}
        // </Table >

    );

    const items = [
        { name: 'nick', quantity: 0 },
        { name: 'asdasdasd', quantity: 0 },
        { name: 'asdasd', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 }
    ];

    return (

        <div className="MainPageDiv">
            <div className="ButtonDiv">
                <Button onClick={updateQuantity} action="add" variant="success">Add Item</Button>
                <Button onClick={updateQuantity} action="delete" variant="danger">Delete Item</Button>
            </div>
            <ItemTable id="ItemTable" />
        </div>
    );
}

export default App;
