        const itemQuantity = e.target.getAttribute("itemquantity");

        const item = {
            name: itemName,
            quantity: itemQuantity
        };
        items.push(item);
    }
    const updateQuantity = (e) => {

        var selected = [];
        $("input:checkbox[name=type]:checked").each(function () {
            // console.log(e.target);
            selected.push(e.target);
        });
        console.log(selected);
        if (selected.length == 0) {
            alert("Select atleast 1 item to delete.");
        }

        if (toBeDeleted.length == 0) {
            alert("Empty");
        }
        else {

        }
    }


    // make api call to get the list of items
    useEffect(() => {
        retrieveListItems();
    }, []);

    // 
    const ItemTable = () => (
        <Table striped bordered hover><tr>
            <th></th>
            <th>Item Name</th>
            <th>Quantity</th>
        </tr>
            {items.map(function (item) {
                return <tr>
                    <th><input itemname={item.name} itemquantity={item.quantity} name="type" type="checkbox" /></th>
                    <th>{item.name}</th>
                    <th>{item.quantity}</th>
                </tr>


            })}
        </Table >

    );

    const items = [
        { name: 'nick', quantity: 0 },
        { name: 'asdasdasd', quantity: 0 },
        { name: 'asdasd', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 }
    ];

    return (

        <div className="MainPageDiv">
            <div className="ButtonDiv">
                <Button onClick={updateQuantity} action="add" variant="success">Add Item</Button>
                <Button onClick={updateQuantity} action="delete" variant="danger">Delete Item</Button>
            </div>
            <ItemTable className="ItemTable" />
        </div>
    );
}

export default App;
