            if (chk.checked) {
                selected.push(i);
            }
        });
        if (selected.length == 0) {
            alert("No items are selected.")
        }
    }


    // make api call to get the list of items
    useEffect(() => {
        retrieveListItems();
    }, []);

    // 
    const ItemTable = () => (
        <Table striped bordered hover size="sm" className="ItemTable" >
            <thead>
                <tr>
                    <th></th>
                    <th>Item Name</th>
                    <th>Quantity</th>
                </tr>
            </thead>
            <tbody>
                {items.map(function (item) {
                    return <tr>
                        <td><input type="checkbox" /></td>
                        <td>{item.name}</td>
                        <td>{item.quantity}</td>
                    </tr>
