    const ItemTable = () => (
        <Table striped bordered hover size="sm" className="ItemTable" >
            <thead>
                <tr>
                    <th></th>
                    <th>Item Name</th>
                    <th>Quantity</th>
                </tr>
            </thead>
            <tbody>
                {items.map(function (item, i = 0) {
                    return <tr>
                        <td><input type="checkbox" onChange={handleInputChange} value={i++} /></td>
                        <td>{item.name}</td>
                        <td>{item.quantity}</td>
