    useEffect(() => {
        retrieveListItems();
    }, []);

    // 
    const ItemTable = () => (
        <table striped bordered hover><tr>
            <th></th>
            <th>Item Name</th>
            <th>Quantity</th>
        </tr>
            {items.map(function (item) {
                return <tr>
                    <th><input type="checkbox" /></th>
                    <th>{item.name}</th>
                    <th>{item.quantity}</th>
                </tr>


            })}
        </table >

    );


    //
    const items = [
        { name: 'nick', quantity: 0 },
        { name: 'asdasdasd', quantity: 0 },
        { name: 'asdasd', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 }
    ];

    return (

        <div className="MainPageDiv">
            <div className="ButtonDiv">
                <Button variant="success">Add Item</Button>
                <Button variant="danger">Delete Item</Button>
            </div>
            <ItemTable className="itemList" />
        </div>
    );
}

export default App;
