import React, { useState, useEffect } from 'react';
import { Button, Table } from "react-bootstrap";
import MainPage from "./MainPage.css"

function App() {
    // const [items, setItems] = useState(null);
    const [toBeDeleted, setToBeDeleted] = useState(null);
    const retrieveListItems = () => {

        // make api call to get items

        // set data to state hook
    }
    const checkBoxChanged = (e) => {
        if (toBeDeleted == null) {
            toBeDeleted = [];
        }
        alert(e.target.quantity)
    }

    // make api call to get the list of items
    useEffect(() => {
        retrieveListItems();
    }, []);

    // 
    const ItemTable = () => (
        <Table striped bordered hover><tr>
            <th></th>
            <th>Item Name</th>
            <th>Quantity</th>
        </tr>
            {items.map(function (item) {
                return <tr>
                    <th><input onChange="checkBoxChanged()" item={item.name} quantity={item.quantity} type="checkbox" /></th>
                    <th>{item.name}</th>
                    <th>{item.quantity}</th>
                </tr>


            })}
        </Table >

    );

    const items = [
        { name: 'nick', quantity: 0 },
        { name: 'asdasdasd', quantity: 0 },
        { name: 'asdasd', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 },
        { name: 'nick', quantity: 0 }
    ];

    return (

        <div className="MainPageDiv">
            <div className="ButtonDiv">
                <Button variant="success">Add Item</Button>
                <Button variant="danger">Delete Item</Button>
            </div>
            <ItemTable className="ItemTable" />
        </div>
    );
}

export default App;
